CREATE TABLE IF NOT EXISTS `users` ( `id` int(11) NOT NULL AUTO_INCREMENT, `address` varchar(255) NOT NULL, `username` varchar(32) NOT NULL, `password` varchar(255) NOT NULL, `home` varchar(255) NOT NULL, `uid` int(11) DEFAULT NULL, `gid` int(11) DEFAULT NULL, `gecos` varchar(255) DEFAULT NULL, `quota` varchar(255) DEFAULT '0', `name` varchar(255) DEFAULT NULL, `disabled` BOOLEAN NOT NULL, `domainAdmin` BOOLEAN NOT NULL, `superAdmin` BOOLEAN NOT NULL, `created`DATETIME NOT NULL,	`updated` DATETIME NOT NULL, `redirectOnly` BOOLEAN DEFAULT NULL, `redirectTo` text, `domainName` varchar(255) DEFAULT NULL, PRIMARY KEY (`id`), UNIQUE KEY `address` (`username`, `domainName`), KEY `domain` (`domainName`), KEY `login` (`username`, `domainName`, `disabled`), KEY `search` (`address`, `name`), KEY `redirectOnly` (`redirectOnly`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `forwarder` (`id` int(11) NOT NULL AUTO_INCREMENT, `address` varchar(128) NOT NULL, `action` varchar(8) NOT NULL, `aliases` text, PRIMARY KEY (`id`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `quota` (`username` varchar(255) NOT NULL, `bytes` bigint(20) NOT NULL DEFAULT '0', `messages` int(11) NOT NULL DEFAULT '0', PRIMARY KEY (`username`)) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `expires` (`username` varchar(100) NOT NULL, `mailbox` varchar(255) NOT NULL, `expire_stamp` int(11) NOT NULL, PRIMARY KEY (`username`,`mailbox`)) ENGINE=InnoDB;